mapper.select:
SELECT

FROM bas_iphoto a

WHERE a.id = #{id}

SELECT

FROM bas_iphoto a


WHERE a.del_flag = #{DEL_FLAG_NORMAL} 

	AND a.type = #{type}


	AND a.spec = #{spec}


	AND a.qrCode LIKE 
			'%'||#{qrCode}||'%'
			'%'+#{qrCode}+'%'
			CONCAT('%', #{qrCode}, '%')

ORDER BY a.type, a.spec, a.update_date DESC

mapper.update:
UPDATE bas_iphoto SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
